import { PartialKeys, VirtualizerOptions, Virtualizer } from '@tanstack/virtual-core';
export * from '@tanstack/virtual-core';

declare function useVirtualizer<TScrollElement, TItemElement = unknown>(options: PartialKeys<VirtualizerOptions<TScrollElement, TItemElement>, 'observeElementRect' | 'observeElementOffset' | 'scrollToFn'>): Virtualizer<TScrollElement, TItemElement>;
